import mpmath
mpmath.mp.dps = 10000  # Pi-nin 1000 rəqəmini əldə edək
pi_digits = str(mpmath.mp.pi)[2:]  # nöqtədən sonrakı rəqəmlər
print(pi_digits)



# pi_digits = ''



OFFSET = 65535  # Add an offset to ensure u_new is always positive

def encrypt(text):
    result = ""
    for i, char in enumerate(text):
        u = ord(char)  # Unicode value of the character
        if i >= len(pi_digits):  # Check if `i` exceeds the available digits of π
            raise ValueError(f"Pi-də {i}-ci rəqəm mövcud deyil.")  # Ensure `i` is within bounds

        p = int(pi_digits[i])  # Use the index `i` to fetch `p` from `pi_digits`

        if i % 2 == 0 and p % 2 == 0:
            u_new = u + i + p + OFFSET  # Add OFFSET to ensure u_new is positive
            marker = '1'
        elif i % 2 == 0 and p % 2 == 1:
            u_new = u + i - p + OFFSET  # Add OFFSET
            marker = '2'
        elif i % 2 == 1 and p % 2 == 1:
            u_new = u - i - p + OFFSET  # Add OFFSET
            marker = '3'
        else:
            # Odd index, even `p`
            u_new = u - i + p + OFFSET  # Add OFFSET
            marker = '4'

        result += chr(u_new) + marker
    return result


def decrypt(text):
    result = ""
    i = 0
    while i < len(text):
        char = text[i]
        marker = text[i + 1]
        u_new = ord(char) - OFFSET  # Subtract OFFSET to restore the original value

        i_index = len(result)  # Correctly calculate the index based on the decrypted result length

        p = int(pi_digits[i_index])  # Use the same index logic as in `encrypt`

        if marker == '1':
            # u_new = u + i + p + OFFSET → u = u_new - i - p
            u = u_new - i_index - p
        elif marker == '2':
            # u_new = u + i - p + OFFSET → u = u_new - i + p
            u = u_new - i_index + p
        elif marker == '3':
            # u_new = u - i - p + OFFSET → u = u_new + i + p
            u = u_new + i_index + p
        elif marker == '4':
            # u_new = u - i + p + OFFSET → u = u_new + i - p
            u = u_new + i_index - p
        else:
            raise ValueError("Yanlış marker tapıldı!")  # Invalid marker

        result += chr(u)
        i += 2  # Move to the next character and marker
    return result


text = '01234567890123456789012345678901234567890123456789'; # test
encrypted_text = encrypt(text)
print(f"Encrypted: {encrypted_text}")
decrypted_text = decrypt(encrypted_text)
print(f"Decrypted: {decrypted_text}")
print(text == decrypted_text)  # Should be True